<?phpdefined('_JEXEC') or die();class facebook{	public static function loadFeed($screen_name = null)	{		if(!$screen_name) {			return false;		}						$fb = new JFacebookPage();							$path	 = 'https://graph.facebook.com/' . $screen_name . '/feed?&access_token=362033320533249|aTsWraWTTNai9k_gBh2Lmcx7khM';		$data	 = '';		$headers = null;		$limit	 = 10;		$offset	 = 0;		$until	 = null;		$since	 = null;							$feed = $fb->sendRequest($path, $data, $headers, $limit, $offset, $until, $since);			if(!$feed)		{			return false;					}								return $feed->data;	}		}