<?phpdefined('_JEXEC') or die();class JTwitterFeed extends JTwitterStatuses{	protected $options;	/**	 * Method to send the request.	 *	 * @param   string  $path     The path of the request to make	 * @param   string  $method   The request method.	 * @param   mixed   $data     Either an associative array or a string to be sent with the post request.	 * @param   array   $headers  An array of name-value pairs to include in the header of the request	 *	 * @return  array  The decoded JSON response	 *	 * @since   12.3	 * @throws  RuntimeException	 */	public function sendRequest($path, $method = 'GET', $data = array(), $headers = array())	{		// Set parameters.		$parameters['oauth_token'] = '';		// Send the request.		$response = $this->oauth->oauthRequest($this->fetchUrl($path), $method, $parameters, $data, $headers);		if (strpos($path, 'update_with_media') !== false)		{			// Check Media Rate Limit.			$response_headers = $response->headers;			if ($response_headers['x-mediaratelimit-remaining'] == 0)			{				// The IP has exceeded the Twitter API media rate limit				throw new RuntimeException('This server has exceed the Twitter API media rate limit for the given period.  The limit will reset in '					. $response_headers['x-mediaratelimit-reset'] . 'seconds.'				);			}		}		if (strpos($response->body, 'redirected') !== false)		{			return $response->headers['Location'];		}		return json_decode($response->body);	}}